const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMessages, GatewayIntentBits.MessageContent], partials: [Partials.Channel, Partials.Message] });
client.commands = new Collection();
client.events = new Collection();
client.config = require('./config.json');
client.slashCommands = new Collection();
client.aliases = new Collection();
client.prefix = client.config.prefix;
client.owner = client.config.owner;
client.color = client.config.color;
client.token = client.config.token;
client.loadCommands = (folder) => {
    const commandFiles = fs.readdirSync(`./commands/${folder}`).filter(file => file.endsWith('.js'));
    for (const file of commandFiles) {
        const command = require(`./commands/${folder}/${file}`);
        client.slashCommands.set(command.data.name, command);
        client.commands.set(command.name, command);
        if (command.aliases && command.aliases.length > 0) command.aliases.forEach(alias => client.aliases.set(alias, command.name));
    }
};
client.loadEvents = (folder) => {
    const eventFiles = fs.readdirSync(`./events/${folder}`).filter(file => file.endsWith('.js'));
    for (const file of eventFiles) {
        const event = require(`./events/${folder}/${file}`);
        if (event.once) {
            client.once(event.name, (...args) => event.execute(...args, client));
        } else {
            client.on(event.name, (...args) => event.execute(...args, client));
        }
    }
};
