int main() {
    string word; // word to search for
    int count = 0; // number of occurrences

    // read the word to search for from the user
    cout << "Enter the word to search for: ";
    cin >> word;

    // open the file for reading
    ifstream infile("file.txt");
    if (!infile) {
        cout << "Error opening file." << endl;
        return 1;
    }

    // read the file line by line
    string line;
    while (getline(infile, line)) {
        // count the number of occurrences of the word in the line
        size_t pos = line.find(word);
        while (pos != string::npos) {
            count++;
            pos = line.find(word, pos + 1);
        }
    }

    // print the number of occurrences
    cout << "The word '" << word << "' occurs " << count << " times in the file." << endl;

    return 0;
}

