const { Command } = require('discord.js');

class BanCommand extends Command {
  constructor(client) {
    super(client, {
      name: 'ban',
      description: 'Bans a user from the server',
      usage: '<@user> [reason (optional)]'
    });
  }

  async execute(message, args) {
    const user = message.mentions.first();
    if (!user) {
      return message.author.send('Please mention a user to ban.');
    }
    const reason = args[1] ?? 'No reason provided.';
    if (this.client.bans[user.id]) {
      return message.author.send(`User ${user.user} is already banned.`);
    } else {
      await this.client.bans.create(user.id, reason);
      message.author.send(`User ${user.user} has been banned for ${reason}.`);
    }
  }
}

module.exports = BanCommand;

