class Queue:
    def __init__(self):
        self.front = []
        self.rear = []

    def is_empty(self):
        return len(self.front) == 0

    def is_full(self):
        return len(self.front) == len(self.rear)

    def push(self, item):
        if self.is_full():
            raise Exception("Queue is full")
        self.front.append(item)
        self.rear.append(item)

    def pop(self):
        if self.is_empty():
            raise Exception("Queue is empty")
        item = self.front.pop()
        self.rear.pop()
        return item

    def peek(self):
        if self.is_empty():
            raise Exception("Queue is empty")
        return self.front[0]

    def display(self):
        print(self.front)
        print(self.rear)

