public class PowerOfThreeChecker {
    public static boolean isPowerOfThree(int n) {
        if (n == 1) {
            return true;
        } else if (n == 0) {
            return false;
        } else {
            int x = (int) Math.log10(n) / Math.log10(3);
            return isPowerOfThree((int) (3 * x));
        }
    }
}

